import sqlite3def create_connection():    conn = sqlite3.connect('library.db')    return conndef create_table():    try:        conn = create_connection()        c = conn.cursor()        # Kitoblar jadvalini yaratish        c.execute('''            CREATE TABLE IF NOT EXISTS books (                id INTEGER PRIMARY KEY,                title TEXT NOT NULL,                author TEXT NOT NULL,                published_date TEXT NOT NULL,                file_path TEXT,                file_type TEXT            )        ''')        # Foydalanuvchilar jadvalini yaratish        c.execute('''            CREATE TABLE IF NOT EXISTS users (                id INTEGER PRIMARY KEY,                username TEXT NOT NULL,                email TEXT NOT NULL            )        ''')        conn.commit()        conn.close()        print("Tables created successfully")    except Exception as e:        print(f"Error occurred: {e}")